import { Link } from "react-router-dom"
import "./login.css"
import { useContext, useRef } from "react"
import { Context } from "../../context/Context"

export default function Login() {
  const userRef = useRef()
  const passwordRef = userRef()
  const { dispatch, isFetching } = useContext(Context)

  const handleSubmit = (e) => {
    e.preventDefault()
  }
  return (
    <div className="login">
    <span className="loginTitle">Login</span>
      <form className="loginForm" onSubmit={handleSubmit}>
        <label>Username</label>
        <input className="loginInput" type="text" placeholder="Enter your username" ref={userRef}></input>
        <label>Password</label>
        <input className="loginInput" type="password" placeholder="Enter your password" ref={passwordRef}></input>
        <button className="loginButton" type="submit">Login</button>
      </form>
      <button className="loginRegisterButton">
        <Link to="/register" className="link">Register</Link>
      </button>
    </div>
  )
}
