import { useLocation } from "react-router"
import "./singlePost.css"
import { useEffect, useState, useContext } from "react"
import axios from "axios"
import { Link } from "react-router-dom"
import { Context } from "../../context/Context"


export default function SinglePost() {
  const location = useLocation()
  const path = location.pathname.split("/")[2]
  const [post, setPost] = useState({})
  const PF = "http://localhost:5000/images/"
  const {user} = useContext(Context)
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [updateMode, setUpdateMode] = useState(false)

  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/"+path)
      setPost(res.data)
      setTitle(res.data.title)
      setDesc(res.data.desc)
    }
    getPost()
  }, [path])

  const handleDelete = async () => {
    try {
      await axios.delete(`/posts/${post._id}`,
       {data: {username: user.username}})
      window.location.replace("/")
    } catch(err) {}
  }

  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        {post.photo && (
        <img src={PF + post.photo} alt="" className="singlePostImg" />
        )} 
        {updateMode ? <input type="text" value={title} className="singlePostTitleInput" autoFocus onChange={(e) => setTitle(e.target.value)}></input> : (
        <h1 className="singlePostTitle">
          {post.title}
          {post.username === user?.username && (
              <div className="singlePostEdit">
                  <i className="singlePostIcon fa-solid fa-pen-to-square" onClick={() => setUpdateMode(true)}></i>
                  <i className="singlePostIcon fa-solid fa-trash" onClick={handleDelete}></i>
              </div>
            )}
        </h1>
        )}
        <div className="singlePostInfo">
              <Link to={`/?user=${post.username}`} className="link">
                <span className="singlePostAuthor"> Author: <b>{post.username}</b></span> 
              </Link>
            <span className="singlePostDate">{new Date(post.createdAt).toDateString()}</span>
        </div>
        {updateMode ? (
          <textarea className="singlePostDescInput" value={desc} onChange={(e)=>setDesc(e.target.value)}></textarea>) : (
          <p className="singlePostDesc">{post.desc}</p>
          )
        }
        <button className="singlePostButton">Update</button>
      </div>
    </div>
  )
}
