import { useContext, useState } from "react"
import Sidebar from "../../components/sidebar/Sidebar"
import "./settings.css"
import { Context } from "../../context/Context"
import axios from "axios"

export default function Settings() {
  const {user} = useContext(Context)
  const [file, setFile] = useState(null)
  const [username, setUsername] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [success, setSuccess] = useState(false)

  const handleSubmit = async (e) => {
    e.preventDefault()
    const updatedUser = {
      userId: user._id,
      username,
      email,
      password
    }
    if(file) {
      const data = new FormData()
      const filename = Date.now() + file.name
      data.append("name", filename)
      data.append("file", file)
      updatedUser.profilePic = filename
      try {
        await axios.post("/upload", data)
      } catch (err) {}
    }
    try {
      await axios.put("/users/"+user._id, updatedUser)
      setSuccess(true)
    } catch (err) {}

  }

  return (
    <div className="settings">
      <div className="settingsWrapper">
        <div className="settingsTitle">
            <span className="settingsUpdateTitle">Update Account</span>
            <span className="settingsDeleteTitle">Delete Account</span>            
        </div>
        <form className="settingsForm" onSubmit={handleSubmit}>
            <label>Profile Picture</label>
            <div className="settingsPP">
                <img src={user.profilePic} alt=""></img>
                <label htmlFor="fileInput">
                    <i className="settingsPPIcon fa-solid fa-circle-user"></i>
                </label>
                <input type="file" id="fileInput" style={{display:"none"}} onChange={(e) => setFile(e.target.files[0])}></input>
            </div>
            <label>Username</label>
            <input type="text" placeholder={user.username} onChange={(e) => setUsername(e.target.value)}></input>
            <label>Email</label>
            <input type="text" placeholder={user.email} onChange={(e) => setEmail(e.target.value)}></input>
            <label>Password</label>
            <input type="password" onChange={(e) => setPassword(e.target.value)}></input>
            <button className="settingsSubmit" type="submit">Update</button>
            {success && <span style= {{ color:"green", textAlign:"center", marginTop:"20px" }}>Updated Successfully.</span>}
        </form>
      </div>
      <Sidebar></Sidebar>
    </div>
  )
}
