import { useState, useContext } from "react"
import "./write.css"
import axios from "axios"
import { Context } from "../../context/Context"

export default function Write() {
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [file, setFile] = useState(null)
  const {user} = useContext(Context)

  const handleSubmit = (e) => {
    e.preventDefault()
    const newPost = {
      username: user.username,
      title,
      desc,
    }
    if(file) {
      const data = FormData()
      const filename = Data.now() + file.name
      data.append("name", filename)
      data.append("file", file)
      newPost.photo = filename
    }
    axios.post("/posts")
  }

  return (
    <div className="write">
      <img className="writeImg" src="https://spaceplace.nasa.gov/review/all-about-the-moon/the-moon-near-side.en.jpg" alt=""></img>
      <form className="writeForm" onSubmit={handleSubmit}>
        <div className="writeFormGroup">
            <label htmlFor="fileInput">
                <i className="writeIcon fa-solid fa-upload"></i>
            </label>
            <input type="file" id="fileInput" style={{display:"none"}} className="writeInput"/>
            <input type="text" placeholder="title" id="fileInput" className="writeInput" autoFocus={true}/>
        </div>
        <div className="writeFormGroup">
            <textarea placeholder="Tell your story..." type="text" className="writeInput writeText"></textarea>
        </div>
        <button className="writeSubmit" type="submit">Publish</button>
      </form>
    </div>
  )
}
