import { useState, useContext } from "react"
import "./write.css"
import axios from "axios"
import { Context } from "../../context/Context"

export default function Write() {
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [file, setFile] = useState(null)
  const {user} = useContext(Context)

  const handleSubmit = async (e) => {
    e.preventDefault()
    const newPost = {
      username: user.username,
      title,
      desc,
    }
    if(file) {
      const data = new FormData()
      const filename = Date.now() + file.name
      data.append("name", filename)
      data.append("file", file)
      newPost.photo = filename
      try {
        await axios.post("/upload", data)
      } catch (err) {}
    }
    try {
      const res = await axios.post("/posts", newPost)
      window.location.replace("/post/"+res.data._id)
    } catch (err) {}

  }

  return (
    <div className="write">
      {file && (
        <img className="writeImg" src={URL.createObjectURL(file)} alt=""></img>
      )}
      <form className="writeForm" onSubmit={handleSubmit}>
        <div className="writeFormGroup">
            <label htmlFor="fileInput">
                <i className="writeIcon fa-solid fa-upload"></i>
            </label>
            <input type="file" id="fileInput" style={{display:"none"}} className="writeInput" onChange={(e) => setFile(e.target.files[0])}/>
            <input type="text" placeholder="title" id="fileInput" className="writeInput" autoFocus={true}/>
        </div>
        <div className="writeFormGroup">
            <textarea placeholder="Tell your story..." type="text" className="writeInput writeText"></textarea>
        </div>
        <button className="writeSubmit" type="submit">Publish</button>
      </form>
    </div>
  )
}
